import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUserInfo, logout } from '../../services/authService';
import axios from 'axios';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [usersList, setUsersList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const userInfo = await getUserInfo();
        setUser(userInfo);

        if (userInfo?.role === 'admin') {
          const response = await axios.get('http://localhost:5000/api/users', {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
            }
          });
          setUsersList(response.data);
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) return <p className="text-center text-lg text-gray-600">Loading...</p>;
  if (error) return <p className="text-center text-lg text-red-600">Error: {error}</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white shadow-md rounded-lg">
      <div className="text-center mb-6">
        <h2 className="text-3xl font-bold text-gray-800">Dashboard</h2>
      </div>
      {user ? (
        <>
          <div className="mb-6">
            <p className="text-xl text-gray-700">Welcome, <span className="font-semibold">{user.username}</span>!</p>
            <p className="text-lg text-gray-600">Your role: <span className="font-semibold">{user.role}</span></p>
          </div>
          
          {/* Render admin-specific content */}
          {user.role === 'admin' && (
            <div className="bg-gray-100 p-4 rounded-lg shadow-sm">
              <h3 className="text-2xl font-semibold text-gray-700 mb-4">Users List</h3>
              <ul className="list-disc pl-5">
                {usersList.length > 0 ? (
                  usersList.map((user, index) => (
                    <li key={index} className="py-2 border-b border-gray-300">{user.username} ({user.email})</li>
                  ))
                ) : (
                  <p className="text-gray-600">No users found.</p>
                )}
              </ul>
            </div>
          )}

          {/* Render user-specific content */}
          {user.role === 'user' && (
            <div className="bg-gray-100 p-4 rounded-lg shadow-sm">
              <h3 className="text-2xl font-semibold text-gray-700 mb-4">User Content</h3>
              <p className="text-gray-600">This is user-specific content.</p>
            </div>
          )}
          
          <button 
            className="mt-6 w-full bg-blue-500 text-white py-2 px-4 rounded-lg shadow hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400" 
            onClick={handleLogout}
          >
            Logout
          </button>
        </>
      ) : (
        <p className="text-center text-lg text-gray-600">Loading...</p>
      )}
    </div>
  );
};

export default Dashboard;
