import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUserInfo, logout } from '../../services/authService';
import axios from 'axios';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [usersList, setUsersList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const userInfo = await getUserInfo(); // Ensure this is an async function
        setUser(userInfo);

        if (userInfo?.role === 'admin') {
          // Fetch all users if the logged-in user is an admin
          const response = await axios.get('http://localhost:5000/api/users', {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('accessTokenGenerated')}`
            }
          });
          setUsersList(response.data);
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Dashboard</h2>
      {user ? (
        <>
          <p>Welcome, {user.username}!</p>
          <p>Your role: {user.role}</p>
          
          {/* Render admin-specific content */}
          {user.role === 'admin' && (
            <div>
              <h3>Users List</h3>
              <ul>
                {usersList.length > 0 ? (
                  usersList.map((user, index) => (
                    <li key={index}>{user.username} ({user.email})</li>
                  ))
                ) : (
                  <p>No users found.</p>
                )}
              </ul>
            </div>
          )}

          {/* Render user-specific content */}
          {user.role === 'user' && (
            <div>
              <h3>User Content</h3>
              <p>This is user-specific content.</p>
            </div>
          )}
          
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Dashboard;
